CREATE TEMPORARY FUNCTION cached_url(url STRING)
RETURNS STRING
LANGUAGE js AS """
  if (url === null) {
    return null;
  }
  return "https://us-central1-kernelci-production.cloudfunctions.net/" +
         "kcidb_cache_redirect?" + encodeURIComponent(url);
""";

SELECT name, cached_url(url) AS url
FROM UNNEST((
    SELECT output_files
    FROM ${dataset}.builds
    WHERE id = ${id:json}
))
WITH OFFSET as idx
ORDER BY idx ASC
