CREATE TEMPORARY FUNCTION cached_url(url STRING)
RETURNS STRING
LANGUAGE js AS """
  if (url === null) {
    return null;
  }
  return "https://us-central1-kernelci-production.cloudfunctions.net/" +
         "kcidb_cache_redirect?" + encodeURIComponent(url);
""";


SELECT name, cached_url(url) AS url
FROM UNNEST((
    SELECT ANY_VALUE(patchset_files)
    FROM ${dataset}.checkouts
    WHERE
        git_commit_hash = ${git_commit_hash:json} AND
        patchset_hash = ${patchset_hash:json} AND
        start_time BETWEEN TIMESTAMP_MILLIS($__from) AND TIMESTAMP_MILLIS($__to) AND
        origin LIKE ${origin}
))
WITH OFFSET as idx
ORDER BY idx ASC
